from flask import Flask,jsonify

app = Flask(__name__)

indicators = {
  'CPU' : {'value': None},
  'RAM' : {'value': None},
  'Disk' : {'value': None},
  'Network' : {'value': None},
  }

@app.route("/",methods=["GET"])
def home():
  return "Welcome to HomePage!"

@app.route("/performance", methods=["GET","POST"])
def get_updated_dict():
  for key in indicators.keys():
    indicators[key] = 1
  """  if (key == "CPU"):
      indicators[key] = 1
    if (key == "RAM"):
      indicators[key] = 1
    if (key == "Disk"):
      indicators[key] = 1
    if (key == "NETWORK"):
      indicators[key] = 1 """
  return jsonify(indicators)

class Observer:
  @property
  def cpu_usage(self):
    return self._cpu_usage

  @property
  def ram_usage(self):
    return self._ram_usage

  @property
  def disk_usage(self):
    return self._disk_usage

  @property
  def cpu_usage(self):
    return self._cpu_usage

if __name__ == "__main__":
  app.run(debug="true")
  
  